mapper.select:

SELECT * FROM UNIFORM_INFO

	UNI_INFO_NO = #{uniInfoNo}
	UNIFORM_NAME LIKE '%'||#{uniformName}||'%'
	COLOR = #{color}
	PLAYER LIKE '%'||#{palyerName}||'%'

ORDER BY UNI_INFO_NO

mapper.update:
UPDATE UNIFORM_INFO
UNIFORM_NAME = #{uniformName}, COLOR = #{color}, UNIFORM_SIZE = #{uniformSize}, PLAYER_NAME = #{playerName}, PRICE = #{price}, UNIFORM_CNT = #{uniformCnt}, UNIFORM_IMG = #{uniformImg}
WHERE UNI_INFO_NO = #{uniInfoNo}
